import{_ as t,o as d,c as r,d as e}from"./app-75535683.js";const a={},o=e('<h3 id="hydra" tabindex="-1"><a class="header-anchor" href="#hydra" aria-hidden="true">#</a> Hydra</h3><ol><li><p>是什么 Hydra是一款密码破解工具，广泛用于暴力破解各种网络服务的登录密码。它能够尝试多种不同的用户名和密码组合来尝试登录到服务，如SSH、FTP、HTTP等，并可根据指定的字典文件或自定义的密码列表进行尝试。</p></li><li><p>常用参数</p><table><thead><tr><th>选项</th><th>解释</th></tr></thead><tbody><tr><td>-l username</td><td>提供登录名</td></tr><tr><td>-P WordList.txt</td><td>指定要使用的密码列表</td></tr><tr><td>server service</td><td>设置要攻击的服务器地址和服务</td></tr><tr><td>-s PORT</td><td>在非默认服务端口号的情况下使用</td></tr><tr><td>-V 或 -vV</td><td>显示正在尝试的用户名和密码组合</td></tr><tr><td>-d</td><td>如果详细输出没有帮助，则显示调试输出</td></tr></tbody></table></li><li><p>命令示例<br><code>hydra -l username -P wordlist.txt server service</code><br><code>hydra -l lazie -P /usr/share/wordlists/rockyou.txt 10.10.246.233 imap</code></p></li></ol>',2),s=[o];function c(l,i){return d(),r("div",null,s)}const n=t(a,[["render",c],["__file","Hydra.html.vue"]]);export{n as default};
